var top250JSON = {}

//requests sent to imdbapi.org, which is an unofficial restful API for querying
//imdb. Since these requests are only made once, no need to worry about 
//spamming them
top250.forEach(function(movieTitle, movieIndex){
    $.ajax({
	    type: "get",
	    url: "http://imdbapi.org/?title="+
	    	  movieTitle+"&type=json&plot=none&episode=0&limit=1&yg=0&mt=none&lang=en-US&offset=&aka=simple&release=simple&business=1&tech=0",
	    success: function(dataList) {
	    	var data = (JSON.parse(dataList))[0];
	    	
	    	//some films have no business object
	    	try{
		    	var budget = data.business.budget[0].money;
		    	var gross  = data.business.gross[0].money;
	    	}
	    	catch(e){
	    		var budget = null; 
	    		var gross  = null;
	    	}
	    	//some films have no runtime 
	    	try{
		    	var runtime = data.runtime[0];
	    	}
	    	catch(e){
	    		var runtime = null; 
	    	}
	    	try{
		    	top250JSON[movieTitle] = {
		    		"genres"   :data.genres,
		    		"rated"    :data.rated,
		    		"budget"   :budget,
		    		"gross"    :gross,
		    		"country"  :data.country,
		    		"release"  :data.release_date,
		    		"directors":data.directors,
		    		"actors"   :data.actors,
		    		"runtime"  :runtime,
		    		"year"     :data.year
		    	};
	    	}
	    	catch(e){
	    		console.log(movieTitle+"couldn't be found")
	    	}
    	}
    });

});

$("body").append(JSON.stringify(top250JSON));